INSERT INTO `SanPham` (`MaSanPham`, `TenSanPham`, `HinhURL`, `GiaSanPham`, `NgayNhap`, `SoLuongTon`, `SoLuongBan`, `SoLuocXem`, `MoTa`, `BiXoa`, `MaLoaiSanPham`, `MaHangSanXuat`) VALUES (5, 'ABSOLUT PEPPAR ', '5.jpg', 420000 , '2017-01-01', 13, 8, 18, '<div>ABSOLUT PEPPAR </div><ul><li>Vodka Absolut Peppar mang hương vị Ớt xanh và các loại Thảo mộc mang lại hương vị sốc và nóng cho các thực khách thưởng thức rượu</li><li> Hơi nóng của Jalapenos ( loại Ớt xanh Chilê )</li><li> Hương vị của cà chua xanh và thảo mộc</li><li>Dung Tích: 750ml , Hàng nhập khẩu</li><li>Nồng Độ: 40 %</li></ul>', 0, 1, 1);
